DROP TABLE IF EXISTS `douyun_news`;
CREATE TABLE `douyun_news` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '自增id',
  `news_title` varchar(200) DEFAULT NULL COMMENT '文章标题',
  `news_type` tinyint(4) DEFAULT '0' COMMENT '资讯类型：0行业资讯；1公司动态；2热门新闻',
  `news_msg` text COMMENT '资讯内容',
  `news_img` varchar(300) DEFAULT NULL COMMENT '资讯图片',
